class Lap_ta extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Lap_ta_model');
    } 

    /*
     * Listing of lap_ta 
     */
    function index()
    {
        $data['lap_ta'] = $this->Lap_ta_model->get_all_lap_ta();
        
        $data['_view'] = 'backend/lap_ta/index';
        $this->load->view('backend/layouts/main',$data);
    }

    /*
     * Adding a new lap_ta
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'nim' => $this->input->post('nim'),
				'judul_laporan' => $this->input->post('judul_laporan'),
				'tgl_upload' => $this->input->post('tgl_upload'),
				'dospem_1' => $this->input->post('dospem_1'),
				'dospem_2' => $this->input->post('dospem_2'),
				'kategori_laporan' => $this->input->post('kategori_laporan'),
				'upload_laporan' => $this->input->post('upload_laporan'),
				'notifikasi' => $this->input->post('notifikasi'),
            );
            
            $lap_ta_id = $this->Lap_ta_model->add_lap_ta($params);
            redirect('lap_ta/index');
        }
        else
        {            
            $data['_view'] = 'backend/lap_ta/add';
            $this->load->view('backend/layouts/main',$data);
        }
    }  

    /*
     * Editing a lap_ta
     */
    function edit($id_laporan)
    {   
        // check if the lap_ta exists before trying to edit it
        $data['lap_ta'] = $this->Lap_ta_model->get_lap_ta($id_laporan);
        
        if(isset($data['lap_ta']['id_laporan']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'nim' => $this->input->post('nim'),
					'judul_laporan' => $this->input->post('judul_laporan'),
					'tgl_upload' => $this->input->post('tgl_upload'),
					'dospem_1' => $this->input->post('dospem_1'),
					'dospem_2' => $this->input->post('dospem_2'),
					'kategori_laporan' => $this->input->post('kategori_laporan'),
					'upload_laporan' => $this->input->post('upload_laporan'),
					'notifikasi' => $this->input->post('notifikasi'),
                );

                $this->Lap_ta_model->update_lap_ta($id_laporan,$params);            
                redirect('lap_ta/index');
            }
            else
            {
                $data['_view'] = 'backend/lap_ta/edit';
                $this->load->view('backend/layouts/main',$data);
            }
        }
        else
            show_error('The lap_ta you are trying to edit does not exist.');
    } 

    /*
     * Deleting lap_ta
     */
    function remove($id_laporan)
    {
        $lap_ta = $this->Lap_ta_model->get_lap_ta($id_laporan);

        // check if the lap_ta exists before trying to delete it
        if(isset($lap_ta['id_laporan']))
        {
            $this->Lap_ta_model->delete_lap_ta($id_laporan);
            redirect('lap_ta/index');
        }
        else
            show_error('The lap_ta you are trying to delete does not exist.');
    }
    
}